import { HeaderBar } from "../layout/HeaderBar";
import { connect } from "react-redux";
import { Typography, Box } from "@mui/material";
import { LockRounded } from "@mui/icons-material";
import { useHistory } from "react-router-dom";
import FileUpload from "../layout/FileUpload";
import axios from "axios";

function Dashboard(props) {
    const { loggedIn } = props;
    const history = useHistory();
    if (!loggedIn) {

        history.push('/');
    }

    const handleFileChange = (dataUri, file) => {
        this.setState({ avatar: dataUri });
        this.setState({ img: file });
        const formData = new FormData();
        formData.append("avatar", this.state.img);
        axios
            .post("/api/avatar/upload_photo", formData, {
                params: {
                    _id: this.props.auth.user.id,
                },
            })
            .then((res) => {
                const { token } = res.data;
                localStorage.setItem("jwtToken", token);
                // Set token to Auth header
                setAuthToken(token);
                // Decode token to get user data
                const decoded = jwt_decode(token);
                // Set current user
                this.props.setUser(decoded);
            })
            .catch((err) => {
                // then print response status
                console.log("upload failed" + err);
            });
    };

    return (
        <Box
            sx={{
                flexGrow: 1,
                overflowX: "hidden",
                margin: "2% 5%"
            }}
        >
            <HeaderBar />
            <Box sx={{ textAlign: "center", mt: "2%" }}>
                <Box
                    style={{ maxWidth: "none", padding: "30px", background: "white" }}
                >
                    {/* <Notify alerts={this.state.alerts} ref={this.notifyAlerts} /> */}
                    <div className="row">
                        <div className="col-md-4">
                            <div className="avatar-photo">
                                <FileUpload handleFileChange={handleFileChange} />
                                <img
                                    src={avatar}
                                    alt="previewImg"
                                    onClick={FileUpload.handleFile}
                                />
                            </div>
                        </div>
                        <div className="col-md-8">
                            <div className="table-response">
                                <table className="table table-striped table-bordered">
                                    <tbody>
                                        <tr>
                                            <td>Name</td>
                                            <td>
                                                <input
                                                    type="text"
                                                    className="info"
                                                    name="name"
                                                    onChange={this.onChangeProfileInfo}
                                                    defaultValue={this.state.name}
                                                    required
                                                    disabled
                                                ></input>
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>ID</td>
                                            <td>
                                                <input
                                                    type="text"
                                                    className="info"
                                                    name="id"
                                                    onChange={this.onChangeProfileInfo}
                                                    defaultValue={this.state.id}
                                                    required
                                                    disabled
                                                ></input>
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>Job</td>
                                            <td>
                                                <input
                                                    type="text"
                                                    className="info"
                                                    name="job"
                                                    onChange={this.onChangeProfileInfo}
                                                    defaultValue={this.state.job}
                                                    required
                                                    disabled={false}
                                                ></input>
                                            </td>
                                        </tr>
                                        {/* <tr>
                                            <td>Birthday</td>
                                            <td>
                                                <div className="datepicker">
                                                    <DatePicker
                                                        style={{
                                                            width: "100%",
                                                            border: "1px solid #ddd",
                                                            paddingLeft: "12px",
                                                            height: "38px",
                                                        }}
                                                        className="form-control"
                                                        selected={Date.parse(this.state.birthday)}
                                                        name="birthday"
                                                        onChange={this.setInDate}
                                                        required
                                                        disabled={false}
                                                    />
                                                </div>
                                            </td>
                                        </tr> */}
                                        <tr>
                                            <td>User_role</td>
                                            <td style={{ height: "48px" }}>

                                                {user.role}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>Department</td>
                                            <td>
                                                <select
                                                    name="department"
                                                    className="form-control mr-sm-3"
                                                    defaultValue={this.state.department}
                                                    required
                                                    disabled={false}
                                                    onChange={this.onChangeProfileInfo}
                                                >
                                                    <option defaultValue="null">Select department</option>
                                                    <option defaultValue={"10부"}>10부</option>
                                                    <option defaultValue={"9부"}>9부</option>
                                                    <option defaultValue={"기타"}>기타</option>
                                                </select>
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>Password</td>
                                            <td>
                                                <input
                                                    type="password"
                                                    className="info"
                                                    name="password"
                                                    onChange={this.onChangeProfileInfo}
                                                    defaultValue={this.state.password}
                                                    required
                                                    disabled={false}
                                                ></input>
                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
                                <input
                                    type="button"
                                    className="btn btn-info"
                                    value="Update"
                                    onClick={this.SaveProfileInfo}
                                    style={{ marginLeft: "auto", display: "flex" }}
                                />
                            </div>
                        </div>
                    </div>

                    <div className="row">{cards}</div>
                    <Modal isOpen={this.state.show_modal} toggle={this.onPlay}>
                        <div className="profile_table_info">
                            <input
                                value="Please fill out all table"
                                style={{
                                    borderBottom: "none",
                                    borderLeft: "none",
                                    borderRight: "none",
                                    borderTop: "none",
                                }}
                            ></input>
                        </div>
                    </Modal>
                </Box>
            </Box>
        </Box>
    );
}

function mapStateToProps(state) {
    const { loggedIn } = state;
    return { loggedIn: loggedIn };
}

const ConnectedDashboard = connect(mapStateToProps)(Dashboard);

export { ConnectedDashboard as Dashboard };
