import { BrowserRouter, Route, Switch,Redirect } from "react-router-dom";
import { LandingPage } from "../pages/landing";
import { SignIn } from "../pages/auth/SignIn";
import { SignUp } from "../pages/auth/SignUp";
import { Dashboard } from "../pages/Dashboard";
import Usertype from "../pages/usertype";
import {SignUpOrg} from "../pages/auth/SignUpOrg";
import { ForgotPassword } from "../pages/auth/ForgotPassword";
import { VerifyEmail } from "../pages/auth/VerifyEmail";

import {connect} from "react-redux";

function Router(props) {
    const { loggedIn,emailConfirmed, dispatch } = props;
    console.log(loggedIn,emailConfirmed);
    return (
        <BrowserRouter>
            <Switch>
                <Route exact path="/"><LandingPage /></Route>
                {loggedIn && (
                    <>
                    {!emailConfirmed && <VerifyEmail></VerifyEmail>}
                    {emailConfirmed && (
                       <>
                           <Route exact path="/dashboard"><Dashboard /></Route>
                           <Redirect to={{pathname: "/dashboard"}}/>
                       </>
                    )}
                    </>
                )}
                {!loggedIn && (
                    <>
                        <Route exact path="/signin"><SignIn /></Route>
                        <Route exact path="/signup"><SignUp /></Route>
                        <Route exact path="/signuporg"><SignUpOrg /></Route>
                        <Route exact path="/usertype"><Usertype /></Route>
                        <Route exact path="/forgotpassword"><ForgotPassword/></Route>
                    </>
                )}
            </Switch>
        </BrowserRouter>
    );
}
function mapStateToProps(state) {
    const { loggedIn,emailConfirmed } = state;
    return { loggedIn: loggedIn,emailConfirmed:emailConfirmed };
}

const ConnectedRouter = connect(mapStateToProps)(Router);

export { ConnectedRouter as Router };
