import { connect } from "react-redux";
import * as React from 'react';
import { Toolbar, AppBar, Box, Typography, Button, Avatar, Menu, MenuItem } from "@mui/material";
import { LogOut } from "../../utils/auth";
function HeaderBar(props) {
    const { loggedIn, dispatch } = props;

    const handleLogout = function () {
        dispatch(LogOut());
    };

    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
         setAnchorEl(null);
    };

    const open = Boolean(anchorEl);
    const id = open ? 'simple-popover' : undefined;
    return (
        <Box sx={{ flexGrow: 1 }}>
            <AppBar position="static" color="secondary">
                <Toolbar>
                    <Typography
                        variant="h6"
                        component="div"
                        sx={{ flexGrow: 1 }}
                    >
                        React Events
                    </Typography>
                    {loggedIn === false && (
                        <>
                            <Button color="inherit" href="/signin">
                                Sign In
                            </Button>
                            <Button color="inherit" href="/usertype">
                                Sign Up
                            </Button>
                        </>
                    )}
                    {loggedIn === true && (
                        /*<Button color="inherit" onClick={handleLogout}>
                            Log Out
                        </Button>*/
                        <React.Fragment>
                            <Button
                                id="basic-button"
                                aria-controls={open ? 'basic-menu' : undefined}
                                aria-haspopup="true"
                                aria-expanded={open ? 'true' : undefined}
                                onClick={handleClick}
                            >
                                Dashboard
                            </Button>
                            <Menu
                                id="basic-menu"
                                anchorEl={anchorEl}
                                open={open}
                                onClose={handleClose}
                                MenuListProps={{
                                    'aria-labelledby': 'basic-button',
                                }}
                            >
                                <MenuItem onClick={handleClose}>My account</MenuItem>
                                <MenuItem onClick={handleLogout}>Logout</MenuItem>
                            </Menu>
                        </React.Fragment>
                    )}
                </Toolbar>
            </AppBar>
        </Box>
    );
}

function mapStateToProps(state) {
    const { loggedIn } = state;
    return { loggedIn: loggedIn };
}

const ConnectedHeaderBar = connect(mapStateToProps)(HeaderBar);

export { ConnectedHeaderBar as HeaderBar };
