const initialState = {
    loggedIn: undefined,
    emailConfirmed: false
};
function AuthReducer(state = initialState, action) {
    const { type, payload } = action;
    switch (action.type) {
        case "LOG_IN":
            return { loggedIn: true };
        case "LOG_OUT":
            return { loggedIn: false };
        case "EMAIL_COMFIRMED":
            return { loggedIn: true };
        default:
            return initialState;
    }
}

export { AuthReducer };
