import React, { useState, useEffect } from "react";
import axios from "axios";
import { API_URL } from "../../env";
import { getUserInfo } from "../../utils/auth";

const Dashboard = (props) => {
    const { loggedIn, dispatch } = props;
    const [info, setInfo] = useState({
        firstName: "",
        lastName: ""
    })
    const [file, setFile] = useState();
    useEffect(() => {
        if (loggedIn) {
            dispatch(getUserInfo((userInfo) => {
                setInfo({ firstName: userInfo.firstName, lastName: userInfo.lastName })
            }));
        }
    }, [])
    const onFormSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('myfile', file[0].name);
        console.log(file[0].name, 'file')
        console.log(formData, 'form')
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        };
        axios
            .post(`${API_URL}/auth/upload_photo`, formData)
            .then((response) => {
                alert("The file is successfully uploaded");
            }).catch((error) => {
            });
    }
    const onChange = (e) => {
        console.log(e.target.files, 'file')
        setFile(e.target.files)
    }
    return (
        <form onSubmit={onFormSubmit}>
            <div className="avatar">
                <input type="file" className="custom-file-input" name="myImage" onChange={onChange} />
                <img src={file} alt="altImage" onClick={onChange} />
            </div>
            <button className="upload-button" type="submit">Upload to DB</button>
            <div>{info.firstName}</div>
        </form>
    )
}

export default Dashboard;
